mapper.select:
	select ROUND(AVG(grade), 1)
    from bookReview
    where ref = #{nowSeq}

mapper.update:
update bookUpload set imageFileName=#{imageFileName},
	 				  imageOriginalFileName=#{imageOriginalFileName},
	 				  subject=#{subject},
	 				  author=#{author},
	 				  content=#{content}
where seq=#{seq}
